const path = require("path"),
	lru = require(path.join(__dirname, "lib", "tiny-lru.js")),
	nth = 2e5,
	cache = lru(nth);

function populate (arg, start = 0) {
	const nth = arg.max;
	let i = -1;

	while (++i < nth) {
		arg.set(i + start, Math.floor(Math.random() * nth));
	}
}

populate(cache);
populate(cache, nth);
populate(cache);
populate(cache, nth);
